import React from 'react';

// Component Modal untuk detail produk
function Modal({ product, onClose }) {
  if (!product) return null;

  const formatPrice = (price) => {
    return price.toLocaleString('de-DE');
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg max-w-lg w-full">
        <div className="relative">
          <img
            src={product.image}
            alt={product.name}
            className="w-full h-64 object-cover rounded-t-lg"
          />
          <button 
            onClick={onClose}
            className="absolute top-4 right-4 bg-white rounded-full w-8 h-8 flex items-center justify-center hover:bg-gray-100 text-xl"
          >
            Ã—
          </button>
        </div>
        
        <div className="p-6">
          <h3 className="text-2xl font-bold mb-2">{product.name}</h3>
          <p className="text-green-700 font-bold text-xl mb-3">
            Rp {formatPrice(product.price)}
          </p>
          <p className="text-gray-600 mb-6">{product.description}</p>
          
          <button 
            onClick={onClose}
            className="w-full bg-green-700 text-white py-3 rounded font-medium hover:bg-green-800"
          >
            Pesan di App
          </button>
        </div>
      </div>
    </div>
  );
}

export default Modal;
