import React from 'react';

function ProductCard({ product, onClick }) {
  const formatPrice = (price) => {
    return price.toLocaleString('id-ID');
  };

  return (
    <div 
      onClick={onClick}
      className="cursor-pointer hover:bg-gray-50 transition py-3 px-2 rounded-lg flex gap-4 items-start"
    >
      {/* Gambar di kiri - kecil dan rounded */}
      <img
        src={product.image}
        alt={product.name}
        className="w-24 h-24 object-cover rounded-lg flex-shrink-0"
      />
      
      {/* Info di kanan - sejajar */}
      <div className="flex-1 min-w-0">
        <h3 className="font-bold text-base mb-1 text-gray-800">{product.name}</h3>
        <p className="text-green-700 font-bold text-sm mb-1">
          Rp {formatPrice(product.price)}
        </p>
        <p className="text-gray-500 text-xs leading-relaxed">{product.description}</p>
      </div>
    </div>
  );
}

export default ProductCard;
