import React from 'react';

function Sidebar({ categories, activeCategory, onCategoryChange }) {
  return (
    <aside className="w-64 flex-shrink-0">
      <div className="sticky top-20 space-y-3">
        {categories.map((category, index) => (
          <button
            key={index}
            onClick={() => onCategoryChange(category.category)}
            className={`w-full text-left px-5 py-5 transition leading-tight ${
              activeCategory === category.category
                ? 'text-green-700 font-black text-3xl'
                : 'text-gray-700 hover:text-green-700 text-2xl'
            }`}
          >
            {category.category}
          </button>
        ))}
      </div>
    </aside>
  );
}

export default Sidebar;
