import React from 'react';
import americano1 from '../Americano1.jpg';
import berryManuka from '../Berry Manuka Americano6.jpg';
import hotAmericano2 from '../Hot Americano2.jpg';
import hotManukaAmericano4 from '../Hot Manuka Americano4.jpg';
import manuka3 from '../Manuka 3.jpg';
import triplePeach from '../Triple Peach Americano5.jpg';

function Hero() {
  return (
    <div className="relative bg-white overflow-hidden pb-40">
      {/* Background dengan gambar kamu */}
      <div className="absolute top-0 left-0 right-0 h-80 opacity-50">
        <div className="grid grid-cols-6 h-full gap-1">
          <div className="bg-cover bg-center" style={{backgroundImage: `url(${americano1})`}}></div>
          <div className="bg-cover bg-center" style={{backgroundImage: `url(${hotAmericano2})`}}></div>
          <div className="bg-cover bg-center" style={{backgroundImage: `url(${manuka3})`}}></div>
          <div className="bg-cover bg-center" style={{backgroundImage: `url(${hotManukaAmericano4})`}}></div>
          <div className="bg-cover bg-center" style={{backgroundImage: `url(${triplePeach})`}}></div>
          <div className="bg-cover bg-center" style={{backgroundImage: `url(${berryManuka})`}}></div>
        </div>
      </div>

      {/* Content dengan padding lebih besar */}
      <div className="relative py-20 text-center mb-24">
        {/* "OUR MENU" text in background - dengan outline style */}
        <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
          <h2 
            className="text-9xl font-bold tracking-widest" 
            style={{
              letterSpacing: '0.3em',
              WebkitTextStroke: '2px #d4e5d4',
              color: 'transparent'
            }}
          >
            OUR MENU
          </h2>
        </div>
        
        {/* Main "MENU" text - juga jadi outline */}
        <h1 
          className="relative text-8xl font-bold mb-4 tracking-wide"
          style={{
            WebkitTextStroke: '3px #166534',
            color: 'transparent'
          }}
        >
          MENU
        </h1>
      </div>

      {/* Services banner lebih ke bawah lagi */}
      <div className="relative mx-auto max-w-5xl px-8">
        <div className="border-2 border-green-600 rounded-full bg-white py-5 px-12">
          <div className="flex items-center justify-center gap-8">
            <span className="text-gray-400 text-sm font-medium tracking-wide">TYPE OF SERVICES AVAILABLE</span>
            <span className="text-gray-300">|</span>
            <div className="flex items-center gap-6 text-green-700 font-semibold text-base">
              <span>Pick-up</span>
              <span className="text-green-600 text-xs">●</span>
              <span>Delivery</span>
              <span className="text-green-600 text-xs">●</span>
              <span>Coffee Catering</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Hero;
