import React from 'react';

function ProductCard({ product, onClick }) {
  const formatPrice = (price) => {
    return price.toLocaleString('id-ID');
  };

  return (
    <div 
      onClick={onClick}
      className="border rounded-lg overflow-hidden cursor-pointer hover:shadow-lg transition bg-white flex"
    >
      {/* Gambar di kiri - lebih kecil */}
      <img
        src={product.image}
        alt={product.name}
        className="w-32 h-32 object-cover flex-shrink-0"
      />
      
      {/* Info di kanan */}
      <div className="p-4 flex-1">
        <h3 className="font-bold text-base mb-1">{product.name}</h3>
        <p className="text-green-700 font-bold text-sm mb-2">
          Rp {formatPrice(product.price)}
        </p>
        <p className="text-gray-600 text-xs line-clamp-2">{product.description}</p>
      </div>
    </div>
  );
}

export default ProductCard;
